<?php function register_skins(){	wp_register_style('skin-dark-cyan', THEME_CSS_URI . '/dark_cyan.css', false, '1.0');	wp_register_style('skin-dark-purple', THEME_CSS_URI . '/dark_purple.css', false, '1.0');	wp_register_style('skin-dark-blue', THEME_CSS_URI . '/dark_blue.css', false, '1.0');	wp_register_style('skin-dark-green', THEME_CSS_URI . '/dark_green.css', false, '1.0');	wp_register_style('skin-dark-red', THEME_CSS_URI . '/dark_red.css', false, '1.0');	wp_register_style('skin-dark-yellow', THEME_CSS_URI . '/dark_yellow.css', false, '1.0');	wp_register_style('skin-dark-orange', THEME_CSS_URI . '/dark_orange.css', false, '1.0');	wp_register_style('skin-light-cyan', THEME_CSS_URI . '/cyan.css', false, '1.0');	wp_register_style('skin-light-purple', THEME_CSS_URI . '/purple.css', false, '1.0');	wp_register_style('skin-light-blue', THEME_CSS_URI . '/blue.css', false, '1.0');	wp_register_style('skin-light-green', THEME_CSS_URI . '/green.css', false, '1.0');	wp_register_style('skin-light-red', THEME_CSS_URI . '/red.css', false, '1.0');	wp_register_style('skin-light-orange', THEME_CSS_URI . '/orange.css', false, '1.0');}//Select theme skinfunction theme_skin(){	$skin  = opt('theme_skin');	$color = opt('theme_skin_color');		//Default skin color is light/yellow	if($skin == 1 && $color == 'yellow')		return;		if($skin)	{		//light		if($color == 'cyan')			wp_enqueue_style('skin-light-cyan');		else if($color == 'purple')			wp_enqueue_style('skin-light-purple');		else if($color == 'blue')			wp_enqueue_style('skin-light-blue');		else if($color == 'green')			wp_enqueue_style('skin-light-green');		else if($color == 'red')			wp_enqueue_style('skin-light-red');		else if($color == 'orange')			wp_enqueue_style('skin-light-orange');	}	else	{		//dark		if($color == 'cyan')			wp_enqueue_style('skin-dark-cyan');		else if($color == 'purple')			wp_enqueue_style('skin-dark-purple');		else if($color == 'blue')			wp_enqueue_style('skin-dark-blue');		else if($color == 'green')			wp_enqueue_style('skin-dark-green');		else if($color == 'red')			wp_enqueue_style('skin-dark-red');		else if($color == 'yellow')			wp_enqueue_style('skin-dark-yellow');		else if($color == 'orange')			wp_enqueue_style('skin-dark-orange');	}}function theme_scripts(){	//Register Styles		wp_enqueue_style('fonts', 'http://fonts.googleapis.com/css?family=Open+Sans:300|Ubuntu:500,300');	wp_enqueue_style('style', get_bloginfo('stylesheet_url'), false, '1.0.4');	if(opt('responsive_layout'))		wp_enqueue_style('responsive-style', THEME_CSS_URI . '/responsive.css', false, '1.0.4');	register_skins();	theme_skin();		wp_register_style('isotope', THEME_CSS_URI. '/isotope.css', false, '1.5.19', 'screen');	wp_register_style('prettyPhoto', THEME_CSS_URI. '/prettyPhoto.css', false, '3.1.4', 'screen');	wp_register_style('touch-slider', THEME_CSS_URI. '/touch-slider.css', false, '1.0', 'screen');		//Register scripts	wp_register_script( 'idTabs', THEME_JS_URI . '/jquery.idTabs.min.js', false, '2.2', true );	wp_register_script( 'superfish', THEME_JS_URI . '/superfish.js', false, '1.0', true );	wp_register_script( 'widgets', THEME_JS_URI . '/widgets.js', false, '1.0', true );	wp_register_script( 'gmap', THEME_JS_URI . '/jquery.gmap-2.1.2-min.js', false, '2.1.2', true );	wp_register_script( 'isotope', THEME_JS_URI . '/jquery.isotope.min.js', false, '1.5.19', true );	wp_register_script( 'prettyPhoto', THEME_JS_URI . '/jquery.prettyPhoto.js', false, '3.1.4', true );		wp_register_script( 'jquery-easing', THEME_JS_URI . '/jquery.easing.1.3.js', false, '1.3', true );	wp_register_script( 'touch-slider', THEME_JS_URI . '/jquery.touch-slider.1.0.1.min.js',false, '1.0.1', true);	wp_register_script( 'jcarousel', THEME_JS_URI . '/jquery.jcarousel.min.js', false,'1.0',true );	wp_register_script( 'backgroundpos', THEME_JS_URI . '/jquery.backgroundpos.min.js', false,'1.0.1',true );	wp_register_script( 'awesome-slider', THEME_JS_URI . '/jquery.awesome-slider.1.0.2.min.js', false,'1.0.2',true );				//Enqueue Scripts	wp_enqueue_script('superfish');	wp_enqueue_script('widgets');	wp_enqueue_script('idTabs');	//Google Map script for contact page	if(is_page_template('template-contact.php'))	{		wp_enqueue_script('gmap');	}				//Load isotope/prettyPhoto on portfolio page	if(is_page_template('template-portfolio.php'))	{		//Isotope Plugin		wp_enqueue_style('isotope');		wp_enqueue_script('isotope');		}		if(is_singular('portfolio'))	{		wp_enqueue_script('jquery-easing');				wp_enqueue_style('touch-slider');		wp_enqueue_script('touch-slider');				wp_enqueue_script('jcarousel');	}		//About template	if(is_page_template('template-about.php'))	{		wp_enqueue_script('jquery-easing');				wp_enqueue_script('jcarousel');				wp_enqueue_script( 'backgroundpos');	}		if(is_page_template('template-home.php'))	{		wp_enqueue_script( 'backgroundpos');			wp_enqueue_script('jquery-easing');		wp_enqueue_style('touch-slider');		wp_enqueue_script('touch-slider');				wp_enqueue_script('awesome-slider');				wp_localize_script( 'awesome-slider', 'slider_settings', 		array(			  'delay'=>opt('home_slider_delay'), 			  'itemInSpeed'=> opt('home_slider_item_in_speed'),			  'itemOutSpeed'=>opt('home_slider_item_out_speed'),			  'bgSpeed'=>opt('home_slider_bg_speed')			  ) 		);	}		//Pretty photo is used in every page	wp_enqueue_style('prettyPhoto');	wp_enqueue_script('prettyPhoto');		//Custom Script 	wp_enqueue_script(				'custom',				THEME_JS_URI . '/custom.js',				false,				'1.0',				true			);				wp_localize_script( 'custom', 'theme_uri', 		array(			  //'base'=>THEME_URI,			  //'js' => THEME_JS_URI,			  //'css' => THEME_CSS_URI,			  'img' => THEME_IMAGES_URI			  )		);	wp_localize_script( 'custom', 'theme_strings', array('contact_submit'=>__('Send', TEXTDOMAIN) ) );		Ajax_Load_Posts_Init();}add_action('wp_enqueue_scripts', 'theme_scripts');function clean_header(){	wp_deregister_script( 'comment-reply' );}add_action('init','clean_header');function Ajax_Load_Posts_Init(){	if( is_singular() || (is_home() && !opt('blog_load_more')) ) 		return;		global $wp_query;		// What page are we on? And what is the pages limit?	$max = $wp_query->max_num_pages;	$paged = ( get_query_var('paged') > 1 ) ? get_query_var('paged') : 1;		// Add some parameters for the JS.	wp_localize_script(		'custom',		'paged_data',		array(			'startPage' => $paged,			'maxPages' => $max,			'nextLink' => next_posts($max, false),			'loadingText' => __('Loading...', TEXTDOMAIN),			'loadMoreText' => __('Load More', TEXTDOMAIN),			'noMorePostsText' => __('No More Posts', TEXTDOMAIN)		)	);}?>